const http = require('http');
const fs = require('fs');
const path = require('path');

// MIME types mapping
const mimeTypes = {
  '.html': 'text/html',
  '.js': 'text/javascript',
  '.css': 'text/css',
  '.json': 'application/json',
  '.png': 'image/png',
  '.jpg': 'image/jpeg',
  '.jpeg': 'image/jpeg',
  '.gif': 'image/gif',
  '.svg': 'image/svg+xml',
  '.ico': 'image/x-icon',
  '.webp': 'image/webp',
  '.avif': 'image/avif'
};

const server = http.createServer((req, res) => {
  let filePath = '.' + req.url;

  // Serve index.html for root requests
  if (filePath === './') {
    filePath = './index.html';
  }

  // Get file extension
  const extname = path.extname(filePath).toLowerCase();
  const contentType = mimeTypes[extname] || 'application/octet-stream';

  // Read and serve the file
  fs.readFile(filePath, (error, content) => {
    if (error) {
      if (error.code === 'ENOENT') {
        // File not found - try to serve index.html for SPA routing
        fs.readFile('./index.html', (error, content) => {
          if (error) {
            res.writeHead(404, { 'Content-Type': 'text/plain' });
            res.end('404 Not Found');
          } else {
            res.writeHead(200, {
              'Content-Type': 'text/html',
              'Cache-Control': 'no-cache, no-store, must-revalidate',
              'Pragma': 'no-cache',
              'Expires': '0'
            });
            res.end(content, 'utf8');
          }
        });
      } else {
        res.writeHead(500, { 'Content-Type': 'text/plain' });
        res.end('500 Internal Server Error');
      }
    } else {
      // Set appropriate cache headers
      const cacheHeaders = {
        'Content-Type': contentType
      };

      // For static assets (images, CSS, JS), allow some caching but make it revalidate
      if (['.css', '.js', '.png', '.jpg', '.jpeg', '.gif', '.svg', '.ico', '.webp', '.avif'].includes(extname)) {
        cacheHeaders['Cache-Control'] = 'public, max-age=300, must-revalidate'; // 5 minutes with revalidation
      } else {
        // For HTML files, prevent caching to ensure updates are visible
        cacheHeaders['Cache-Control'] = 'no-cache, no-store, must-revalidate';
        cacheHeaders['Pragma'] = 'no-cache';
        cacheHeaders['Expires'] = '0';
      }

      res.writeHead(200, cacheHeaders);
      res.end(content);
    }
  });
});

const PORT = 5000;
const HOST = '0.0.0.0';

server.listen(PORT, HOST, () => {
  console.log(`GlowOnCall website running at http://${HOST}:${PORT}/`);
});

// Graceful shutdown
process.on('SIGTERM', () => {
  console.log('Received SIGTERM, shutting down gracefully');
  server.close(() => {
    console.log('Server closed');
    process.exit(0);
  });
});

process.on('SIGINT', () => {
  console.log('Received SIGINT, shutting down gracefully');
  server.close(() => {
    console.log('Server closed');
    process.exit(0);
  });
});

styles.css.content:
/* Service Areas and Location Styling */
.service-areas {
    margin: 20px 0;
    padding: 20px;
    background: #FFFFFF;
    border-radius: 12px;
}

.service-areas h3 {
    color: var(--primary-color);
    margin-bottom: 10px;
    font-size: 1.2rem;
    font-weight: 600;
}

.service-areas p {
    color: var(--text-light);
    margin-bottom: 15px;
    line-height: 1.5;
}

.locations-grid {
    display: flex;
    flex-wrap: wrap;
    gap: 8px;
    margin-top: 15px;
}

.locations-grid .location-tag {
    background: linear-gradient(135deg, var(--primary-color) 0%, var(--price-tag-color) 100%);
    color: white;
    padding: 6px 12px;
    border-radius: 20px;
    font-size: 0.85rem;
    font-weight: 500;
    display: inline-block;
    transition: transform 0.2s ease, box-shadow 0.2s ease;
    cursor: pointer;
}

.locations-grid .location-tag:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(107, 75, 62, 0.3);
}

@media (max-width: 768px) {
    .locations-grid {
        gap: 6px;
    }

    .locations-grid .location-tag {
        font-size: 0.8rem;
        padding: 5px 10px;
    }

    .service-areas {
        margin: 15px 0;
        padding: 15px;
    }
}

/* Coming Soon Overlay for Featured Services */
.coming-soon-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(80,80,80,0.85);
    color: #fff;
    font-size: 1.5rem;
    font-weight: 700;
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 2;
    border-radius: 16px;
    letter-spacing: 2px;

}

/* Coming Soon Card Styles */
.coming-soon-card {
    opacity: 0.7;
    pointer-events: none;
}

.coming-soon-card .service-card-image,
.coming-soon-card .featured-card-image {
    position: relative;
}

.coming-soon-card button[disabled] {
    opacity: 0.5;
    cursor: not-allowed;
    background-color: var(--border-color);
}

.coming-soon-card button[disabled]:hover {
    background-color: var(--border-color);
}

/* Featured service cards coming soon overlay */
.featured-service-card .coming-soon-overlay {
    border-radius: 12px;
    font-size: 1.2rem;
}

/* Service card wrapper coming soon overlay */
.service-card-wrapper .coming-soon-overlay {
    border-radius: 12px;
    font-size: 1.3rem;
}

/* Product card coming soon overlay */
.service-card .coming-soon-overlay {
    border-radius: 12px;
    font-size: 1.3rem;
}
.square-card .service-icon img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 24px;
    box-shadow: 0 1px 6px rgba(0,0,0,0.07);
    display: block;
    position: absolute;
    top: 0;
    left: 0;
}
.square-card .service-icon {
    position: relative;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
}
/* Call Button in Header */
.call-btn {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background-color: var(--accent-color);
    color: white;
    font-size: 1.3rem;
    box-shadow: 0 2px 8px rgba(0,0,0,0.08);

    text-decoration: none;
    margin-left: 12px;
}
.call-btn:hover {
    background: var(--price-tag-color);
    color: white;
}
/* Square Service Cards with Curved Edges */
.square-cards {
    display: flex;
    gap: 32px;
    justify-content: center;
    align-items: center;
    margin: 32px 0;
}
.square-card {
    border-radius: 24px;
    box-shadow: 0 2px 16px rgba(0,0,0,0.10);
    width: 200px;
    height: 200px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    background: #fff;
    transition: box-shadow 0.2s;
    margin: 0;
}
.square-card .service-icon {
    margin-bottom: 10px;
}
.square-card:hover {
    box-shadow: 0 4px 24px rgba(0,0,0,0.16);
}
.square-card p {
    font-size: 1rem;
    font-weight: 500;
    margin: 0;
    color: var(--text-dark);
}
/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Poppins', sans-serif;
    background: #F5D6A4;
    color: #000000;
    padding-bottom: 80px;
    overflow-x: hidden;
}

/* Variables */
:root {
    --primary-color: #6B4B3E;
    --secondary-color: #F5D6A4;
    --accent-color: #D9A679;
    --price-tag-color: #A57B62;
    --background-color: #F5D6A4;
    --text-dark: #000000;
    --text-light: #6B4B3E;
    --border-color: #E7D7C4;
    --success-color: #25D366;
    --shadow: 0 2px 10px rgba(107, 75, 62, 0.15);
    --border-radius: 12px;
}

/* Modern Header */
.modern-header {
    background: white;
    box-shadow: 0 2px 20px rgba(107, 75, 62, 0.1);
    position: sticky;
    top: 0;
    z-index: 1000;
    border-bottom: 1px solid var(--border-color);
}

.nav-container {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 1rem 2rem;
    max-width: 1400px;
    margin: 0 auto;
}

.brand-section {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.brand-logo {
    width: 50px;
    height: 50px;
    object-fit: contain;
    border-radius: 8px;
}

.brand-info {
    display: flex;
    flex-direction: column;
}

.brand-name {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--primary-color);
    margin: 0;
    line-height: 1;
}

.brand-tagline {
    font-size: 0.85rem;
    color: var(--text-light);
    font-weight: 500;
}

.nav-center {
    display: flex;
    align-items: center;
    gap: 2rem;
    flex: 1;
    justify-content: center;
}

.location-badge {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    background: var(--accent-color);
    color: white;
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-size: 0.9rem;
    font-weight: 500;
}

.modern-search {
    display: flex;
    align-items: center;
    background: var(--secondary-color);
    border-radius: 25px;
    padding: 0.75rem 1.5rem;
    gap: 0.75rem;
    width: 350px;
    border: 2px solid transparent;
    transition: all 0.3s ease;
}

.modern-search:focus-within {
    border-color: var(--accent-color);
    box-shadow: 0 0 0 3px rgba(217, 166, 121, 0.1);
}

.modern-search i {
    color: var(--text-light);
    font-size: 1rem;
}

.modern-search input {
    border: none;
    background: transparent;
    outline: none;
    flex: 1;
    font-size: 1rem;
    color: var(--text-dark);
}

.modern-search input::placeholder {
    color: var(--text-light);
    opacity: 0.7;
}

.action-buttons {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.cta-button {
    background: var(--primary-color);
    color: white;
    border: none;
    padding: 0.75rem 1.5rem;
    border-radius: 25px;
    font-weight: 600;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    cursor: pointer;
    transition: all 0.3s ease;
}

.cta-button:hover {
    background: var(--price-tag-color);
    transform: translateY(-2px);
    box-shadow: 0 4px 15px rgba(107, 75, 62, 0.3);
}

.contact-actions {
    display: flex;
    gap: 0.5rem;
}

.action-btn {
    width: 45px;
    height: 45px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    text-decoration: none;
    font-size: 1.2rem;
    transition: all 0.3s ease;
}

.action-btn.whatsapp {
    background: var(--success-color);
    color: white;
}

.action-btn.phone {
    background: var(--accent-color);
    color: white;
}

.action-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.15);
}

.header-top {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 12px;
}

.logo-section {
    display: flex;
    align-items: center;
}

.header-logo {
    width: 70px;
    height: 70px;
    object-fit: contain;
    border-radius: 0px;
}

.location-selector {
    display: flex;
    align-items: center;
    color: var(--primary-color);
    font-weight: 500;
    gap: 8px;
}

.header-actions {
    display: flex;
    align-items: center;
    gap: 12px;
}

.whatsapp-btn {
    width: 40px;
    height: 40px;
    background: var(--success-color);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    text-decoration: none;
    font-size: 18px;
}

.profile-btn {
    width: 40px;
    height: 40px;
    background: var(--primary-color);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-weight: 600;
}

.search-container {
    display: flex;
    gap: 10px;
    align-items: center;
