/* Service Grid */
/* Service Grid */
.service-grid, .category-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 16px;
    padding: 0 16px;
}

@media (min-width: 768px) {
    .category-grid {
        grid-template-columns: repeat(3, 1fr);
    }
}

@media (min-width: 1024px) {
    .category-grid {
        grid-template-columns: repeat(4, 1fr);
    }
}

.service-card, .category-card {
    background: white;
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0 2px 12px rgba(0,0,0,0.08);
    cursor: pointer;
    transition: all 0.3s ease;
    position: relative;
}

.service-card:hover, .category-card:hover {
    transform: translateY(-4px);
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
}

.category-card {
    min-height: 120px;
    display: flex;
    flex-direction: column;
    padding: 0;
}

.service-icon, .category-icon {
    width: 100%;
    height: 80px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    overflow: hidden;
    margin: 0;
}

.service-icon img, .category-icon img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.3s ease;
}

.category-card:hover .category-icon img {
    transform: scale(1.1);
}

.category-card p {
    padding: 12px 16px;
    margin: 0;
    font-size: 13px;
    color: var(--text-dark);
    font-weight: 600;
    text-align: center;
    background: white;
    flex: 1;
    display: flex;
    align-items: center;
    justify-content: center;
}

.weekday-offer {
    background: linear-gradient(135deg, var(--success-color), var(--accent-color));
    color: white;
}

.offer-text {
    font-weight: 600;
    font-size: 12px;
    text-align: center;
    line-height: 1.2;
}

.combo-badge {
    background: linear-gradient(135deg, var(--accent-color), var(--price-tag-color));
    color: white;
    font-weight: 600;
    font-size: 10px;
    text-align: center;
    padding: 8px;
}

.combo-package {
    background: var(--secondary-color);
    color: var(--primary-color);
    font-weight: 600;
    font-size: 10px;
    text-align: center;
    padding: 8px;
}

.service-card p {
    font-size: 13px;
    color: var(--text-dark);
    font-weight: 500;
}

/* Service Filters */
.service-filters {
    display: flex;
    gap: 8px;
    padding: 0 16px 16px;
    overflow-x: auto;
}

.filter-btn {
    padding: 8px 16px;
    border: 1px solid var(--border-color);
    border-radius: 20px;
    background: white;
    color: var(--text-light);
    font-size: 13px;
    cursor: pointer;
    white-space: nowrap;
    transition: all 0.2s ease;
}

.filter-btn.active, .filter-btn:hover {
    background: var(--primary-color);
    color: white;
    border-color: var(--primary-color);
}

/* Enhanced Service Grid Animations */
.service-grid.enhanced {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 16px;
    padding: 0 16px;
}

.service-card.animated {
    position: relative;
    overflow: hidden;
    animation: fadeInUp 0.6s ease-out;
    animation-fill-mode: both;
}

.service-card.animated:nth-child(1) { animation-delay: 0.1s; }
.service-card.animated:nth-child(2) { animation-delay: 0.2s; }
.service-card.animated:nth-child(3) { animation-delay: 0.3s; }
.service-card.animated:nth-child(4) { animation-delay: 0.4s; }

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.service-card.animated .service-icon {
    position: relative;
}

.icon-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: var(--primary-color);
    opacity: 0;
    transition: opacity 0.3s ease;
}

.service-card.animated:hover .icon-overlay {
    opacity: 1;
}

.card-shine {
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: linear-gradient(45deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    transform: translateX(-100%) translateY(-100%) rotate(45deg);
    transition: transform 0.8s ease;
}

.service-card.animated:hover .card-shine {
    transform: translateX(100%) translateY(100%) rotate(45deg);
}

/* Scrollable Container */
.scrollable-container {
    position: relative;
    padding: 0 50px;
}

.scroll-btn {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
